import React, { useState, useEffect } from 'react';
import { Box, Tab, Tabs, TextField, Typography, List, ListItem, ListItemText, Button, MenuItem, Select, FormControl, InputLabel } from '@mui/material';
import axios from 'axios';
import { SelectChangeEvent } from '@mui/material/Select';

import { school } from '../../Types'

function Schools() {
  const [schools, setSchools] = useState<school[]>([]);
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(10);
  const [totalSchools, setTotalSchools] = useState(0);

  useEffect(() => {
    fetchSchools();
  }, [page, pageSize]);

  const fetchSchools = async () => {
    const startId = (page - 1) * pageSize + 1;
    const endId = startId + pageSize - 1;
    try {
      const response = await axios.get(`/api/Schools/?s_SchoolId=${startId}&e_SchoolId=${endId}`);
      setSchools(response.data.schools); // Adjust based on actual API response structure
      setTotalSchools(response.data.total); // Assume total number of schools is returned
    } catch (error) {
      console.error('Failed to fetch schools:', error);
      setSchools([]); // Clear schools on error
    }
  };

  const handlePageChange = (newPage: number) => {
    setPage(newPage);
  };

  const handlePageSizeChange = (event: SelectChangeEvent<number>) => {
    setPageSize(Number(event.target.value)); // Convert string to number if needed
    setPage(1); // Reset to first page with new page size
  };

  return (
    <Box sx={{ width: '100%', typography: 'body1' }}>
      <FormControl fullWidth>
        <InputLabel id="page-size-label">Schools Per Page</InputLabel>
        <Select
          labelId="page-size-label"
          id="page-size-select"
          value={pageSize}
          label="Schools Per Page"
          onChange={handlePageSizeChange}
          sx={{ mb: 2 }}
        >
          {[10, 20, 50].map((size) => (
            <MenuItem key={size} value={size}>{size}</MenuItem>
          ))}
        </Select>
      </FormControl>
      <List>
        {schools.map((school) => (
          <ListItem key={school.SchoolId}>
            <ListItemText primary={school.SchoolName} secondary={school.SchoolRank} />
          </ListItem>
        ))}
      </List>
      <Box display="flex" justifyContent="center" my={2}>
        <Button onClick={() => handlePageChange(page - 1)} disabled={page === 1}>Previous</Button>
        <Button onClick={() => handlePageChange(page + 1)} disabled={page * pageSize >= totalSchools}>Next</Button>
      </Box>
    </Box>
  );
}

export default Schools;
