import React, { useState, useEffect } from 'react';
import { Box, List, ListItemButton, Card, CardContent, Typography, Button, Tabs, Tab } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

import { word } from '../../Types';  // Ensure your School type has a non-optional SchoolId of type number

axios.defaults.baseURL = 'http://35.208.70.236/';

function Words() {
  const [words, setWords] = useState<word[]>([]);
  const [currentPage, setCurrentPage] = useState(0);
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState(0);  // 0 for GRE, 1 for TOEFL

  useEffect(() => {
    fetchWords();
  }, [currentPage]);

  useEffect(() => {
    fetchWords();
  }, [activeTab]);

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setActiveTab(newValue);
  };

  const fetchWords = async () => {
    const pageSize = 20;
    const testId = activeTab === 0 ? 0 : 3
    const s_WordId = currentPage * pageSize;
    const e_WordId = (currentPage + 1) * pageSize;
    try {
      const response = await axios.get(`/api/Words/`, { 
        params: { s_WordId: s_WordId, e_WordId: e_WordId }
      });
      setWords(response.data);
    } catch (error) {
      console.error('Failed to fetch schools:', error);
      setWords([]);
    }
  };

  const handleWordClick = (wordId: number | undefined, testId: number | undefined) => {
    if ((typeof wordId === 'number') && (typeof testId === 'number')) {
      navigate(`/words/${testId}/${wordId}`);
    }
  };

  return (
    <Box sx={{ width: '100%', typography: 'body1' }}>
      <Tabs value={activeTab} onChange={handleTabChange} centered>
        <Tab label="GRE" />
        <Tab label="TOEFL" />
      </Tabs>
      <List>
        {words.map((word) => (
          <ListItemButton key={word.WordId} onClick={() => handleWordClick(word.WordId, word.TestId)}>
            <Card sx={{ width: '100%', display: 'flex', flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
              <CardContent sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'flex-start' }}>
                <Typography variant="body1" sx={{ marginBottom: 1 }}>
                  {word.Word}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ marginBottom: 1 }}>
                  Stem: {word.Stem}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                PartOfSpeech: {word.PartOfSpeech}
                </Typography>
              </CardContent>
            </Card>
          </ListItemButton>
        ))}
      </List>
      <Box display="flex" justifyContent="center" my={2}>
        <Button onClick={() => setCurrentPage(Math.max(currentPage - 1, 0))} disabled={currentPage === 0}>
          Previous
        </Button>
        <Button onClick={() => setCurrentPage(currentPage + 1)} disabled={words.length < 20}>
          Next
        </Button>
      </Box>
    </Box>
  );
}

export default Words;
