import React, { useState } from 'react';
import { Card, CardContent, Typography, Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField, DialogContentText } from '@mui/material';

const UserDetailsView: React.FC = () => {
    const [user, setUser] = useState(() => {
        const savedData = sessionStorage.getItem('user');
        return savedData ? JSON.parse(savedData) : null;
    });
    const [open, setOpen] = useState(false);
    const [newSchoolId, setNewSchoolId] = useState("");

    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    const handleSchoolChange = () => {
        if (user) {
            setUser({ ...user, TargetSchoolId: newSchoolId });
            sessionStorage.setItem('user', JSON.stringify({ ...user, TargetSchoolId: newSchoolId }));
        }
        handleClose();
    };

    return (
        <Card style={{ maxWidth: 345, margin: 'auto', marginTop: '20px' }}>
            <CardContent>
                <Typography color="textSecondary" gutterBottom>
                    User Details
                </Typography>
                <Typography variant="h5" component="h2">
                    {user?.FirstName} {user?.LastName}
                </Typography>
                <Typography color="textSecondary">
                    Username: {user?.Username}
                </Typography>
                <Typography color="textSecondary">
                    Email: {user?.Email}
                </Typography>
                <Typography color="textSecondary">
                    School ID: {user?.TargetSchoolId}
                </Typography>
                <Button variant="outlined" color="primary" style={{ marginTop: '10px' }} onClick={handleOpen}>
                    Modify School
                </Button>
            </CardContent>
            <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">Update School</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        To update your school ID, please enter the new school ID below.
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="school"
                        label="New School ID"
                        type="text"
                        fullWidth
                        value={newSchoolId}
                        onChange={(e) => setNewSchoolId(e.target.value)}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={handleSchoolChange} color="primary">
                        Update
                    </Button>
                </DialogActions>
            </Dialog>
        </Card>
    );
};

export default UserDetailsView;
export {};  // Add an empty export to make this a module
