import React, { useState, useEffect } from 'react';
import { List, ListItem, ListItemText, Typography, Paper } from '@mui/material';
import axios from 'axios';
import { Friend } from '../../Types';

const Friends: React.FC = () => {
  const [user, setUser] = useState<any>(null);
  const [friends, setFriends] = useState<Friend[]>([]);

  useEffect(() => {
    const rawData = sessionStorage.getItem('user');
    if (rawData) {
      const savedData = JSON.parse(rawData);
      if (savedData && savedData[0]) {
        setUser(savedData[0]);
      }
    }
  }, []);

  useEffect(() => {
    if (user) {
      const fetchFriends = async () => {
        try {
          const response = await axios.get('/api/IsFriendWith', { params: { Email: user.Email } });
          setFriends(response.data);
        } catch (error) {
          console.error('Failed to fetch friends:', error);
        }
      };
      fetchFriends();
    }
  }, [user]);

  const formatDate = (date: Date): string => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <Paper elevation={3} style={{ margin: '16px', padding: '16px' }}>
      <Typography variant="h6" component="div" style={{ marginBottom: '12px' }}>
        Friends List
      </Typography>
      <List>
        {friends.map(friend => (
          <ListItem key={friend.id} style={{ marginBottom: '10px', backgroundColor: '#f0f0f0', padding: '10px' }}>
            <ListItemText
              primary={friend.name}
              secondary={`Last active: ${formatDate(new Date(friend.lastActive))}`}
            />
          </ListItem>
        ))}
      </List>
    </Paper>
  );
};

export default Friends;
