// LoginView.tsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../AuthContext'; // Make sure to import from the correct file
import { Typography } from '@mui/material';

const LoginView: React.FC = () => {
    const [email, setEmail] = useState<string>("");
    const [password, setPassword] = useState<string>("");
    const { login } = useAuth();
    const navigate = useNavigate();

    return (
        <div className="loginContainer">
            <div>
                <h3>Login Here</h3>
                <label>Email</label>
                <input
                    type="text"
                    placeholder="Email"
                    onChange={(e) => setEmail(e.target.value)}
                />
                <label>Password</label>
                <input
                    type="password"
                    placeholder="Password"
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button onClick={() => {
                    login(); // Log in using the context's login function
                    console.log(`Logged in as ${email}`);
                    navigate('/'); // Redirect after login
                }}>Log In</button>
                <label>Not a member? &nbsp;
                    <Link to="/signup">Register</Link>
                </label>
            </div>
        <Typography variant="h6" gutterBottom>[Need API for pulling 10 words that are learned the least]</Typography>

        </div>

    );
};

export default LoginView;
