import React, { useState, useEffect } from 'react';
import { Box, List, ListItem, ListItemText, Typography, Card, CardActionArea, CardContent } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

import { School } from '../../types'; // Ensure types are correctly defined and imported

axios.defaults.baseURL = 'http://35.208.70.236/';

function Schools() {
  const [schools, setSchools] = useState<School[]>([]);
  const [currentPage, setCurrentPage] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    fetchSchools();
  }, [currentPage]);

  const fetchSchools = async () => {
    const pageSize = 20;
    const s_SchoolId = currentPage * pageSize + 1;
    const e_SchoolId = (currentPage + 1) * pageSize;
    try {
      const response = await axios.get(`/api/Schools/`, { 
        params: { s_SchoolId, e_SchoolId } 
      });
      setSchools(response.data); // Adjust based on actual API response structure
    } catch (error) {
      console.error('Failed to fetch schools:', error);
      setSchools([]); // Clear previous schools on error
    }
  };

  const handleCardClick = (schoolId: number) => {
    navigate(`/schools/${schoolId}`);
  };

  return (
    <Box sx={{ width: '100%', typography: 'body1', mt: 2 }}>
      <List>
        {schools.map((school) => (
          <ListItem key={school.SchoolId} disablePadding>
            <Card sx={{ width: '100%', mb: 2 }}>
              <CardActionArea onClick={() => handleCardClick(school.SchoolId)}>
                <CardContent>
                  <Typography variant="h6" component="div">
                    {school.SchoolName}
                  </Typography>
                  <Typography color="text.secondary">
                    Rank: {school.SchoolRank}, Score: {school.Score}
                  </Typography>
                </CardContent>
              </CardActionArea>
            </Card>
          </ListItem>
        ))}
      </List>
      <Box display="flex" justifyContent="center" my={2}>
        <Typography sx={{ mr: 2 }} variant="button" onClick={() => setCurrentPage(Math.max(currentPage - 1, 0))} disabled={currentPage === 0}>
          Previous
        </Typography>
        <Typography variant="button" onClick={() => setCurrentPage(currentPage + 1)} disabled={schools.length < 20}>
          Next
        </Typography>
      </Box>
    </Box>
  );
}

export default Schools;
