// LoginView.tsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../AuthContext'; // Ensure the path is correct

const LoginView: React.FC = () => {
    const [email, setEmail] = useState<string>("");
    const navigate = useNavigate();
    const { login, setUser } = useAuth();

    const handleLogin = async () => {
        // Simulate login logic
        const userData = { email }; // Replace with actual data retrieval logic
        setUser(userData); // Store user data in context
        login(); // Set the login state to true
        sessionStorage.setItem('user', JSON.stringify(userData)); // Save to session
        navigate('/profile'); // Navigate to a protected route
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
            />
            <button onClick={handleLogin}>Log In</button>
        </div>
    );
};

export default LoginView;
