import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AppBar, Toolbar, Button, Menu, MenuItem } from '@mui/material';
import './Header.css'; // Importing the CSS file for styles


function Header() {
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleMenu = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    handleClose();
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    handleClose();
  };

  return (
    <AppBar position="static">
      <Toolbar>
        <Button color="inherit" component={Link} to="/">Vocabuddy</Button>
        <Button color="inherit" component={Link} to="/words">Words</Button>
        <Button color="inherit" component={Link} to="/schools">Schools</Button>
        <div className="spacer"></div>
        <Button color="inherit" onClick={handleMenu}>User</Button>
        <Menu
          id="user-menu"
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleClose}
        >
          {!isLoggedIn ? (
            <>
              <MenuItem onClick={handleLogin}>Login</MenuItem>
              <MenuItem onClick={handleLogin}>Signup</MenuItem>
            </>
          ) : (
            <>
              <MenuItem component={Link} to="/">Study</MenuItem>
              <MenuItem onClick={handleClose}>Progress</MenuItem>
              <MenuItem onClick={handleClose}>Friends</MenuItem>
              <MenuItem onClick={handleLogout}>Sign Out</MenuItem>
            </>
          )}
        </Menu>
      </Toolbar>
    </AppBar>
  );
}

export default Header;
