import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../AuthContext'; // Make sure to import from the correct file
import { Typography } from '@mui/material';

export default function SignupView() {
    const [email, setEmail] = useState("");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [rePassword, setRePassword] = useState("");

    const navigate = useNavigate();
    const { login } = useAuth();

    return (
        <div className="signupContainter">
            <div>
                <h3>Join In Us Here</h3>

                <label>Email</label>
                <input type="text" placeholder="Email" id="email"
                    onChange={(e)=>setEmail(e.target.value)}
                />

                <label>Username</label>
                <input type="text" placeholder="Username" id="username"
                    onChange={(e)=>setUsername(e.target.value)}
                />

                <label>Password</label>
                <input type="password" placeholder="Password" id="password"
                    onChange={(e)=>setPassword(e.target.value)}
                />

                <label>Repeat your Password</label>
                <input type="password" placeholder="Password" id="password"
                    onChange={(e)=>setRePassword(e.target.value)}
                />

                <button onClick={() => {
                    login(); // Log in using the context's login function
                    console.log(`Logged in as ${email}`);
                    navigate('/'); // Redirect after login
                }}>Sign Up</button>

                <label>Already have an account? &nbsp;
                    <Link to="/login">Login</Link>
                </label>
                <Typography variant="h6" gutterBottom>[Need API for creating a user]</Typography>

            </div>
        </div>
    )
}
