import React, { useState, useEffect } from 'react';
import { Box, Tab, Tabs, TextField, Typography, List, ListItem, ListItemText, Button } from '@mui/material';
import axios from 'axios';

import { school } from '../../Types'


axios.defaults.baseURL = 'http://35.208.70.236/';


function Schools() {
  const [schools, setSchools] = useState<school[]>([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchSchools();
  }, [currentPage, searchQuery]);

  const fetchSchools = async () => {
    const pageSize = 20;
    const s_SchoolId = currentPage * pageSize + 1;
    const e_SchoolId = (currentPage + 1) * pageSize;
    try {
      const response = await axios.get(`/api/Schools/`, { 
        params: { s_SchoolId, e_SchoolId, query: searchQuery } 
      });
      setSchools(response.data); // Adjust based on actual API response structure
    } catch (error) {
      console.error('Failed to fetch schools:', error);
      setSchools([]);  // Clear previous schools on error
    }
  };

  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(event.target.value);
    setCurrentPage(0); // Reset to the first page for a new search
  };

  const handlePreviousPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 0)); // Ensure we do not go below page 0
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1); // Increment the page number
  };

  return (
    <Box sx={{ width: '100%', typography: 'body1' }}>
      <TextField
        fullWidth
        label="Search schools"
        variant="outlined"
        value={searchQuery}
        onChange={handleSearchChange}
        margin="normal"
      />
      <List>
        {schools.map((school, index) => (
          <ListItem key={index}>
            <ListItemText primary={school.SchoolName} secondary={`ID: ${school.SchoolRank}, Location: ${school.location}`} />
          </ListItem>
        ))}
      </List>
      <Box display="flex" justifyContent="center" my={2}>
        <Button onClick={handlePreviousPage} disabled={currentPage === 0}>Previous</Button>
        <Button onClick={handleNextPage} disabled={schools.length < 20}>Next</Button>
      </Box>
    </Box>
  );
}

export default Schools;
