import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Typography, Select, MenuItem, Card, CardContent, LinearProgress } from '@mui/material';

function Study() {
  const [testName, setTestName] = useState('GRE');
  const [wordData, setWordData] = useState({ word: '', meaning: '', stem: '' });
  const [progress, setProgress] = useState(0);
  const userId = '123'; // This should be dynamically set based on the logged-in user

  useEffect(() => {
    const fetchWordData = async () => {
      try {
        const response = await axios.get(`https://api.example.com/words`, {
          params: { userId, test: testName }
        });
        setWordData(response.data);
        // Dummy progress, replace with actual data
        setProgress(Math.floor(Math.random() * 100));
      } catch (error) {
        console.error('Error fetching word data:', error);
      }
    };

    fetchWordData();
  }, [testName, userId]);

  const handleTestChange = (event: React.SyntheticEvent) => {
    setTestName(event.target.value);
  };

  return (
    <Box display="flex" flexDirection="column" alignItems="center" justifyContent="center" minHeight="100vh">
      <Typography variant="h4" gutterBottom>Select Test Type</Typography>
      <Select value={testName} onChange={handleTestChange} displayEmpty>
        <MenuItem value="GRE">GRE</MenuItem>
        <MenuItem value="TOEFL">TOEFL</MenuItem>
      </Select>

      <Card sx={{ maxWidth: 345, mt: 5 }}>
        <CardContent>
          <Typography variant="h5" component="div">
            {wordData.word}
          </Typography>
          <Typography sx={{ mb: 1.5 }} color="text.secondary">
            {wordData.stem}
          </Typography>
          <Typography variant="body2">
            {wordData.meaning}
          </Typography>
        </CardContent>
      </Card>

      <Box sx={{ width: '90%', mt: 3 }}>
        <Typography variant="body1">Learning Progress:</Typography>
        <LinearProgress variant="determinate" value={progress} />
      </Box>
    </Box>
  );
}

export default Study;
