import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Typography, Alert, Box, TextField, Button, Paper } from '@mui/material';

const SignUpView: React.FC = () => {
    const navigate = useNavigate();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [username, setUsername] = useState("");
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [joinedTime, setJoinedTime] = useState("");
    const [targetSchoolId, setTargetSchoolId] = useState("");
    const [error, setError] = useState("");

    const handleSignUp = async () => {
        // Implement your API call logic to create a new user here
        try {
            // Dummy navigation to simulate successful signup
            console.log("User signed up with email:", email);
            navigate('/'); // Redirect to the homepage or login page
        } catch (err) {
            setError("Failed to sign up. Please check your details and try again.");
        }
    };

    return (
        <Box display="flex" alignItems="center" justifyContent="center" minHeight="100vh">
            <Paper elevation={3} style={{ padding: '40px', width: '350px', textAlign: 'center' }}>
                <Typography variant="h5" gutterBottom>Sign Up</Typography>
                <TextField
                    label="Email"
                    type="email"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <TextField
                    label="Password"
                    type="password"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <TextField
                    label="Firebase UID"
                    type="text"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={firebaseUID}
                    onChange={(e) => setFirebaseUID(e.target.value)}
                />
                <TextField
                    label="Username"
                    type="text"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <TextField
                    label="First Name"
                    type="text"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                />
                <TextField
                    label="Last Name"
                    type="text"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                />
                <TextField
                    label="Joined Time"
                    type="datetime-local"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={joinedTime}
                    onChange={(e) => setJoinedTime(e.target.value)}
                />
                <TextField
                    label="Target School ID"
                    type="text"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={targetSchoolId}
                    onChange={(e) => setTargetSchoolId(e.target.value)}
                />
                <Button 
                    variant="contained" 
                    color="primary" 
                    onClick={handleSignUp} 
                    style={{ marginTop: '20px' }}
                    fullWidth
                >
                    Sign Up
                </Button>
                {error && <Alert severity="error" style={{ marginTop: '20px' }}>{error}</Alert>}
                <Typography variant="body2" style={{ marginTop: '20px' }}>
                    Already a member?&nbsp;
                    <Link to="/login" style={{ textDecoration: 'none' }}>Log In</Link>
                </Typography>
            </Paper>
        </Box>
    );
};

export default SignUpView;
