import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Box, Typography, Tab, Tabs, List, ListItem, ListItemText, Slider } from '@mui/material';
import { recent, least, progress } from '../../Types';

axios.defaults.baseURL = 'http://35.208.70.236/';



function Progress() {
  const [activeTab, setActiveTab] = useState(0); // 0 for GRE, 1 for TOEFL
  const [recentWords, setRecentWords] = useState<recent[]>([]);
  const [leastLearnedWords, setLeastLearnedWords] = useState<least[]>([]);
  const [progressPercentage, setProgressPercentage] = useState<progress[]>([]);


  const  email = useParams<{ Email: string }>();
  const testId = activeTab === 0 ? 0 : 3;

  useEffect(() => {
    const fetchRecentWords = async () => {
      try {
        const response = await axios.get(`/api/Learns/recent`, {
          params: { Email: email, numwords: 10 }
        });
        setRecentWords(response.data);
      } catch (error) {
        console.error('Failed to fetch recent words:', error);
      }
    };

    const fetchLeastLearnedWords = async () => {
      try {
        const response = await axios.get(`/api/Learns/least`, {
          params: { Email: email, TestId: testId, numwords: 10 }
        });
        setLeastLearnedWords(response.data);
      } catch (error) {
        console.error('Failed to fetch least learned words:', error);
      }
    };

    const fetchProgress = async () => {
      try {
        const response = await axios.get(`/api/Learns/progress`, {
          params: { Email: email, TestId: testId }
        });
        setProgressPercentage(response.data);
      } catch (error) {
        console.error('Failed to fetch progress:', error);
      }
    };

    if (email) {
      fetchRecentWords();
      fetchLeastLearnedWords();
      fetchProgress();
    }
  }, [email, testId]);

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setActiveTab(newValue);
  };

  return (
    <Box sx={{ width: '100%', typography: 'body1', padding: 2 }}>
      <Typography variant="h4" gutterBottom>
        Learning Progress Dashboard
      </Typography>
      <Tabs value={activeTab} onChange={handleTabChange} centered>
        <Tab label="GRE" />
        <Tab label="TOEFL" />
      </Tabs>

      <Typography variant="h6" gutterBottom>
        Recently Learned Words
      </Typography>
      <List>
        {recentWords.map((word, index) => (
          (<ListItem key={index}>
            <ListItemText primary={word.Word} secondary={`LastLearnedTime: ${word.LastLearnedTime}`} />
          </ListItem>)
        ))}
      </List>

      <Typography variant="h6" gutterBottom>
        Words to Improve
      </Typography>
      <List>
        {leastLearnedWords.map((word, index) => (
          <ListItem key={index}>
            <ListItemText primary={word.Word} secondary={`PercentLearned: ${word.PercentLearned}`}/>
          </ListItem>
        ))}
      </List>

      <Typography variant="h6" gutterBottom>
        {progressPercentage.map((test, index) => (
          <ListItem key={index}>
            <ListItemText primary={test.TestId} secondary={`Progress: ${test.Progress}`}/>
          </ListItem>
        ))}
      </Typography>
    </Box>
  );
}

export default Progress;
