import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AppBar, Toolbar, Button } from '@mui/material';
import './Header.css'; // Make sure the CSS supports the layout

const { isLoggedIn } = useAuth();
function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleLoginOrLogout = () => {
    if (isLoggedIn) {
      setIsLoggedIn(false); // Log out the user
      navigate('/'); // Optional: navigate to the home page on logout
    } else {
      navigate('/login'); // Change this to your login/signup path
    }
  };

  return (
    <AppBar position="static">
      <Toolbar>
        <Button color="inherit" component={Link} to="/">Vocabuddy</Button>
        <Button color="inherit" component={Link} to="/words">Words</Button>
        <Button color="inherit" component={Link} to="/schools">Schools</Button>
        <div className="spacer"></div>
        {isLoggedIn ? (
          <>
            <Button color="inherit" component={Link} to="/study">Study</Button>
            <Button color="inherit" component={Link} to="/progress">Progress</Button>
            <Button color="inherit" component={Link} to="/friends">Friends</Button>
            <Button color="inherit" onClick={handleLoginOrLogout}>Log Out</Button>
          </>
        ) : (
          <Button color="inherit" onClick={handleLoginOrLogout}>Log In / Sign Up</Button>
        )}
      </Toolbar>
    </AppBar>
  );
}

export default Header;
