import React, { useState, useEffect } from 'react';
import { Box, Typography, List, ListItem, ListItemText, Button } from '@mui/material';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

import { school } from '../../Types';

axios.defaults.baseURL = 'http://35.208.70.236/';

function Schools() {
  const [schools, setSchools] = useState<school[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(0);

  useEffect(() => {
    fetchSchools();
  }, [currentPage]);

  const history = useHistory();

  const fetchSchools = async () => {
    const pageSize = 20;
    const s_SchoolId = currentPage * pageSize + 1;
    const e_SchoolId = (currentPage + 1) * pageSize;
    try {
      const response = await axios.get<School[]>(`/api/Schools/`, { 
        params: { s_SchoolId, e_SchoolId } 
      });
      setSchools(response.data); // Adjust based on actual API response structure
    } catch (error) {
      console.error('Failed to fetch schools:', error);
      setSchools([]);  // Clear previous schools on error
    }
  };

  const handlePreviousPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 0)); // Ensure we do not go below page 0
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1); // Increment the page number
  };

  const handleSchoolClick = (schoolId: number) => {
    history.push(`/schools/${schoolId}`);
  };

  return (
    <Box sx={{ width: '100%', typography: 'body1' }}>
      <Typography variant="h4" align="center" gutterBottom>
        Schools
      </Typography>
      <List>
        {schools.map((school, index) => (
          <ListItem key={index} button onClick={() => handleSchoolClick(school.id)}>
            <ListItemText
              primary={school.SchoolName}
              secondary={`Rank: ${school.SchoolRank}, Location: ${school.Score}`}
            />
          </ListItem>
        ))}
      </List>
      <Box display="flex" justifyContent="center" my={2}>
        <Button onClick={handlePreviousPage} disabled={currentPage === 0}>Previous</Button>
        <Button onClick={handleNextPage} disabled={schools.length < 20}>Next</Button>
      </Box>
    </Box>
  );
}

export default Schools;
