import React, { useState, useEffect } from 'react';
import { Typography, Box, TextField, Button, Tabs, Tab } from '@mui/material';

function HomePage() {
  const [activeTab, setActiveTab] = useState(0);  // 0 for GRE, 1 for TOEFL

    const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
      setActiveTab(newValue);
    };

    return (
      <Box
      display="flex"
      flexDirection="column"
      alignItems="center"
      justifyContent="center"
      height="100vh" // Ensures the height matches the viewport
      width="100vw" // Ensures the width matches the viewport
      sx={{
        overflow: 'hidden', // Prevents scrolling
      }}
    >
      <Typography variant="h3" gutterBottom>Vocabuddy</Typography>
      <Tabs value={activeTab} onChange={handleTabChange} centered>
        <Tab label="GRE" />
        <Tab label="TOEFL" />
      </Tabs>
      <Box>
        <TextField label="Search for words" variant="outlined" />
        <Button variant="contained" sx={{ ml: 1 }}>Search</Button>
      </Box>
    </Box>

  );
}

export default HomePage;
