import React, { useState, useEffect } from 'react';
import { Box, List, ListItem, ListItemText, Button, Card, CardContent, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

import { school } from '../../Types';  // Ensure the type is correctly imported

axios.defaults.baseURL = 'http://35.208.70.236/';

function Schools() {
  const [schools, setSchools] = useState<school[]>([]);
  const [currentPage, setCurrentPage] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    fetchSchools();
  }, [currentPage]);

  const fetchSchools = async () => {
    const pageSize = 20;
    const s_SchoolId = currentPage * pageSize + 1;
    const e_SchoolId = (currentPage + 1) * pageSize;
    try {
      const response = await axios.get(`/api/Schools/`, { 
        params: { s_SchoolId, e_SchoolId }
      });
      setSchools(response.data); // Adjust based on actual API response structure
    } catch (error) {
      console.error('Failed to fetch schools:', error);
      setSchools([]);  // Clear previous schools on error
    }
  };

  const handleSchoolClick = (schoolId: number | undefined) => {
    if (typeof schoolId === 'number') { // Check if schoolId is a number before navigating
      navigate(`/schools/${schoolId}`);
    }
  };

  return (
    <Box sx={{ width: '100%', typography: 'body1' }}>
      <List>
        {schools.map((school) => (
          <ListItem key={school.SchoolId} button onClick={() => handleSchoolClick(school.SchoolId)}>
            <Card sx={{ width: '100%', display: 'flex', justifyContent: 'space-between' }}>
              <CardContent>
                <Typography variant="h6">{school.SchoolName}</Typography>
                <Typography color="text.secondary">Rank: {school.SchoolRank}</Typography>
                <Typography color="text.secondary">Score: {school.Score}</Typography>
              </CardContent>
            </Card>
          </ListItem>
        ))}
      </List>
      <Box display="flex" justifyContent="center" my={2}>
        <Button onClick={() => setCurrentPage(Math.max(currentPage - 1, 0))} disabled={currentPage === 0}>
          Previous
        </Button>
        <Button onClick={() => setCurrentPage(currentPage + 1)} disabled={schools.length < 20}>
          Next
        </Button>
      </Box>
    </Box>
  );
}

export default Schools;
