// Word.tsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography } from '@mui/material';

interface WordDetails {
  word: string;
  description: string;
  stem: string;
  partOfSpeech: string;
}

const Word = () => {
  const { testwordId } = useParams<{ testId: string, wordId: string }>();
  const [wordDetails, setWordDetails] = useState<WordDetails | null>(null);

  useEffect(() => {
    const fetchWordDetails = async () => {
      try {
        const response = await axios.get(`https://api.example.com/words/${wordId}`);
        setWordDetails(response.data);
      } catch (error) {
        console.error('Error fetching word details:', error);
        setWordDetails(null);
      }
    };

    fetchWordDetails();
  }, [wordId]);

  return (
    <Box display="flex" flexDirection="column" alignItems="center" justifyContent="center" height="100vh">
      {wordDetails ? (
        <>
          <Typography variant="h3" gutterBottom>{wordDetails.word}</Typography>
          <Typography variant="subtitle1" gutterBottom>Description: {wordDetails.description}</Typography>
          <Typography variant="subtitle1" gutterBottom>Stem: {wordDetails.stem}</Typography>
          <Typography variant="subtitle1" gutterBottom>Part of Speech: {wordDetails.partOfSpeech}</Typography>
        </>
      ) : (
        <>
          <Typography variant="h3" gutterBottom>Word: </Typography>
          <Typography variant="subtitle1" gutterBottom>Description: </Typography>
          <Typography variant="subtitle1" gutterBottom>Stem: </Typography>
          <Typography variant="subtitle1" gutterBottom>Part of Speech: </Typography>
        </>
      )}
      <Typography variant="h6" gutterBottom>[Need API for pulling single word]</Typography>
    </Box>

  );
};

export default Word;
