// Friends.tsx
import React, { useState, useEffect } from 'react';
import { List, ListItem, ListItemText, Typography, Paper } from '@mui/material';
import { Friend } from '../../Types'; // Importing the Friend interface

const Friends: React.FC = () => {
  const [friends, setFriends] = useState<Friend[]>([]);

  // Simulated fetch function
  useEffect(() => {
    const fetchFriends = async () => {
      // Simulate an API call
      const response = await new Promise<Friend[]>(resolve =>
        setTimeout(() => resolve([
          { id: 1, name: 'Alice', lastActive: new Date(Date.now() - 1000000) },
          { id: 2, name: 'Bob', lastActive: new Date(Date.now() - 5000000) },
          { id: 3, name: 'Charlie', lastActive: new Date(Date.now() - 10000000) },
        ]), 500)
      );
      setFriends(response);
    };

    fetchFriends();
  }, []);

  const formatDate = (date: Date): string => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <Paper elevation={3} style={{ margin: '16px', padding: '16px' }}>
      <Typography variant="h6" component="div" style={{ marginBottom: '12px' }}>
        Friends List
        {sessionStorage.getItem('user')}
      </Typography>
      <Typography variant="h6" gutterBottom>[Need API for pulling all friends, returning their userId and last active time, and their overall progress]</Typography>
      <List>
        {friends.map(friend => (
          <ListItem key={friend.id}>
            <ListItemText
              primary={friend.name}
              secondary={`Last active: ${formatDate(friend.lastActive)}`}
            />
          </ListItem>

        ))}
      </List>

    </Paper>
  );
};

export default Friends;
