import React, { useState, useEffect } from 'react';
import { Card, CardContent, Typography, Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField, DialogContentText, Avatar, Divider } from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle'; // Import icon for user avatar

const UserDetailsView: React.FC = () => {
    const [user, setUser] = useState<any>(null);
    const [open, setOpen] = useState(false);
    const [newSchoolId, setNewSchoolId] = useState("");

    useEffect(() => {
        const savedData = sessionStorage.getItem('user');
        if (savedData) {
            const userData = JSON.parse(savedData);
            setUser(userData[0]);  // Assuming the first item in the array contains the user data
        }
    }, []);

    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    const handleSchoolChange = () => {
        if (user) {
            const updatedUser = { ...user, TargetSchoolId: newSchoolId };
            setUser(updatedUser);
            sessionStorage.setItem('user', JSON.stringify([updatedUser]));  // Update sessionStorage, assuming it holds an array
        }
        handleClose();
    };

    return (
        <Card style={{ maxWidth: 500, margin: 'auto', marginTop: '20px', padding: '20px' }}>
            <CardContent>
                <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
                    <Avatar style={{ backgroundColor: '#1976d2', marginRight: '15px' }}>
                        <AccountCircleIcon />
                    </Avatar>
                    <Typography variant="h5" component="h2">
                        {user?.FirstName} {user?.LastName}
                    </Typography>
                </div>
                <Divider style={{ marginBottom: '20px' }} />
                <Typography variant="subtitle1" color="textSecondary" gutterBottom>
                    Username: {user?.Username}
                </Typography>
                <Typography variant="subtitle1" color="textSecondary">
                    Email: {user?.Email}
                </Typography>
                <Typography variant="subtitle1" color="textSecondary">
                    School ID: {user?.TargetSchoolId}
                </Typography>
                <Typography variant="subtitle1" color="textSecondary">
                    Joined Time: {new Date(user?.JoinedTime).toLocaleString()}
                </Typography>
                <Button variant="contained" color="primary" style={{ marginTop: '20px' }} onClick={handleOpen}>
                    Modify School
                </Button>
            </CardContent>
            <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">Update School</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        To update your school ID, please enter the new school ID below.
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="school"
                        label="New School ID"
                        type="text"
                        fullWidth
                        value={newSchoolId}
                        onChange={(e) => setNewSchoolId(e.target.value)}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={handleSchoolChange} color="primary">
                        Update
                    </Button>
                </DialogActions>
            </Dialog>
        </Card>
    );
};

export default UserDetailsView;
