import React, { useState, useEffect } from 'react';
import { Box, List, ListItemButton, Card, CardContent, Typography, Button, Tabs, Tab, styled } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

import { word } from "../../Types"

enum TestId {
  GRE = 0,
  TOEFL = 3,
}

axios.defaults.baseURL = 'http://35.208.70.236/';

// Styling enhancements using Material-UI styled
const StyledCard = styled(Card)(({ theme }) => ({
  width: '100%',
  marginBottom: theme.spacing(2),
  boxShadow: theme.shadows[4],
  '&:hover': {
    boxShadow: theme.shadows[10],
    backgroundColor: theme.palette.action.hover,
  },
}));

const BoldTypography = styled(Typography)({
  fontWeight: 'bold',
});

const Words: React.FC = () => {
  const [words, setWords] = useState<word[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(0);
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<number>(TestId.GRE);

  useEffect(() => {
    fetchWords();
  }, [currentPage, activeTab]);

  const handleTabChange = (event: React.SyntheticEvent, newValue: TestId) => {
    setActiveTab(newValue);
    setCurrentPage(0); // Reset to first page when switching tabs
  };

  const fetchWords = async () => {
    const pageSize = 20;
    const s_WordId = currentPage * pageSize;
    const e_WordId = (currentPage + 1) * pageSize;
    try {
      const response = await axios.get<word[]>('/api/Words/', { 
        params: { TestId: activeTab, s_WordId, e_WordId }
      });
      setWords(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Failed to fetch words:', error);
      setWords([]);
    }
  };

  const handleWordClick = (wordId: number | undefined) => {
    if ((typeof wordId === 'number') && (typeof testId === 'number')) {
      navigate(`/words/${testId}/${wordId}`);
    }
  };

  return (
    <Box sx={{ width: '100%', typography: 'body1' }}>
      <Tabs value={activeTab} onChange={handleTabChange} centered>
        <Tab label="GRE" value={TestId.GRE} />
        <Tab label="TOEFL" value={TestId.TOEFL} />
      </Tabs>
      <Box sx={{ position: 'relative', minHeight: 300 }}>
        <List>
          {words.map((word) => (
            <ListItemButton key={word.WordId} onClick={() => handleWordClick(word.WordId)}>
              <StyledCard>
                <CardContent>
                  <BoldTypography variant="h6">{word.Word}</BoldTypography>
                  <Typography variant="subtitle2" color="textSecondary">{word.PartOfSpeech}</Typography>
                  <Typography>{word.Description}</Typography>
                </CardContent>
              </StyledCard>
            </ListItemButton>
          ))}
        </List>
      </Box>
      <Box display="flex" justifyContent="center" my={2}>
        <Button onClick={() => setCurrentPage(Math.max(currentPage - 1, 0))} disabled={currentPage === 0}>
          Previous
        </Button>
        <Button onClick={() => setCurrentPage(currentPage + 1)} disabled={words.length < 20}>
          Next
        </Button>
      </Box>
    </Box>
  );
};

export default Words;
