import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Typography, Tab, Tabs, List, ListItem, ListItemText } from '@mui/material';

function Progress() {
  const [testName, setTestName] = useState('GRE');
  const [learnedWords, setLearnedWords] = useState([]);
  const [unlearnedWords, setUnlearnedWords] = useState([]);
  const userId = '123'; // This should dynamically match the authenticated user's ID

  useEffect(() => {
    const fetchWords = async () => {
      try {
        const learnedResponse = await axios.get(`https://api.example.com/learned-words`, {
          params: { userId, test: testName }
        });
        setLearnedWords(learnedResponse.data);

        const unlearnedResponse = await axios.get(`https://api.example.com/unlearned-words`, {
          params: { userId, test: testName }
        });
        setUnlearnedWords(unlearnedResponse.data);
      } catch (error) {
        console.error('Failed to fetch words:', error);
      }
    };

    fetchWords();
  }, [testName, userId]);

  const handleTabChange = (event: React.SyntheticEvent, newValue: string) => {
    setTestName(newValue);
  };

  return (
    <Box sx={{ width: '100%', typography: 'body1', padding: 2 }}>
      <Typography variant="h4" gutterBottom>
        Progress Dashboard
      </Typography>
      <Tabs value={testName} onChange={handleTabChange} centered>
        <Tab label="GRE" value="GRE" />
        <Tab label="TOEFL" value="TOEFL" />
      </Tabs>

      <Typography variant="h6" gutterBottom>
        Recently Learned Words
      </Typography>
      <List>
        {learnedWords.map((word, index) => (
          <ListItem key={index}>
            {/* <ListItemText primary={word.word} secondary={`Meaning: ${word.meaning}`} /> */}
            {/* <ListItemText primary={word.word} secondary={`Meaning: ${word.meaning}`} /> */}
          </ListItem>
        ))}
      </List>

      <Typography variant="h6" gutterBottom>
        Words to Improve
      </Typography>
      <List>
        {unlearnedWords.map((word, index) => (
          <ListItem key={index}>
            <ListItemText primary={word.word} secondary={`Meaning: ${word.meaning}`} />
          </ListItem>
        ))}
      </List>
    </Box>
  );
}

export default Progress;
