import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../AuthContext';
import { Typography, Alert } from '@mui/material'; // Import Alert for showing error messages

const LoginView: React.FC = () => {
    const [email, setEmail] = useState<string>("");
    const [password, setPassword] = useState<string>("");
    const [error, setError] = useState<string>(""); // For handling login errors
    const { login } = useAuth();
    const navigate = useNavigate();

    const handleLogin = async () => {
        try {
            await login(email, password);
            const savedUser = sessionStorage.getItem('user');
            if (savedUser) {
                setUser(JSON.parse(savedUser));
                setIsLoggedIn(true);
            }

        } catch (err) {
            setError("Failed to log in. Please check your credentials and try again."); // Set error message on failure
        }
    };

    return (
        <div className="loginContainer">
            <div>
                <h3>Login Here</h3>
                <label>Email</label>
                <input
                    type="text"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <label>Password</label>
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button onClick={handleLogin}>Log In</button>
                <label>Not a member? &nbsp;
                    <Link to="/signup">Register</Link>
                </label>
                {error && <Alert severity="error">{error}</Alert>}  // Display an error message if there is one
            </div>
            <Typography variant="h6" gutterBottom>
                Need API for checking credentials
            </Typography>
        </div>
    );
};

export default LoginView;
