// Word.tsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography, Card, CardContent, Chip } from '@mui/material';

import { word } from '../../Types'

axios.defaults.baseURL = 'http://35.208.70.236/';

const School = () => {
  const { schoolId } = useParams<{ SchoolId: string }>();
  const [schoolDetails, setSchoolDetails] = useState<word | null>(null);

  useEffect(() => {
    const fetchWordDetails = async () => {
      try {
        const response = await axios.get('/api/Word', { params: {TestId: testId, WordId: wordId }});
        setWordDetails(response.data[0]);
            console.log(response);
        } catch (error) {
            console.error('Error fetching word details:', error);
      }
    };

    fetchWordDetails();
  }, [testId, wordId]);

  return (
    <Box display="flex" flexDirection="column" alignItems="center" justifyContent="center" height="100vh">
    {wordDetails ? (
      <Card sx={{ minWidth: 275, maxWidth: 600, mt: 5 }}>
        <CardContent>
          <Typography variant="h4" gutterBottom component="div" color="primary">
            {wordDetails.Word}
          </Typography>
          <Chip label={wordDetails.PartOfSpeech} color="secondary" sx={{ mb: 2 }} />
          <Typography variant="body1" gutterBottom>
            <strong>Description:</strong> {wordDetails.Description}
          </Typography>
          <Typography variant="body1" gutterBottom>
            <strong>Stem:</strong> {wordDetails.Stem}
          </Typography>
          <Typography variant="body1">
            <strong>Example:</strong> {wordDetails.Example}
          </Typography>
        </CardContent>
      </Card>
    ) : (
      <Typography variant="h6" gutterBottom>
        [Need API for pulling single word]
      </Typography>
    )}
  </Box>
);
};

export default School;
