// SiteStatistics.tsx
import React, { useState, useEffect } from 'react';
import { Box, Typography, Paper } from '@mui/material';
import { Statistics } from '../../Types'; // Import the Statistics interface

const SiteStatistics: React.FC = () => {
  const [stats, setStats] = useState<Statistics | null>(null);

  // Mock fetching data from an API
  useEffect(() => {
    const fetchStatistics = async () => {
      // Simulated API call
      const fetchedStats: Statistics = {
        mostPopularWord: 'Ephemeral',
        numberOfUsers: 5240,
        mostPopularSchool: 'Harvard'
      };
      setTimeout(() => setStats(fetchedStats), 1000); // Simulating network delay
    };

    fetchStatistics();
  }, []);

  return (
    <Paper elevation={3} style={{ padding: '20px', margin: '20px' }}>
      <Typography variant="h5" gutterBottom>Site Statistics</Typography>
      {stats ? (
        <Box>
          <Typography variant="subtitle1">Most Popular Word: {stats.mostPopularWord}</Typography>
          <Typography variant="subtitle1">Number of Users: {stats.numberOfUsers}</Typography>
          <Typography variant="subtitle1">Most Popular School: {stats.mostPopularSchool}</Typography>
        </Box>
      ) : (
        <Typography variant="subtitle1">Loading statistics...</Typography>
      )}
      <Typography variant="h6" gutterBottom>[Need API for statistics]</Typography>
    </Paper>
  );
};

export default SiteStatistics;
