      <Card sx={{ minWidth: 275, maxWidth: 600, mt: 5 }}>
        <CardContent>
          <Typography variant="h4" gutterBottom component="div" color="primary">
            {wordDetails.Word}
          </Typography>
          <Chip label={wordDetails.PartOfSpeech} color="secondary" sx={{ mb: 2 }} />
          <Typography variant="body1" gutterBottom>
            <strong>Description:</strong> {wordDetails.Description}
          </Typography>
          <Typography variant="body1" gutterBottom>
            <strong>Stem:</strong> {wordDetails.Stem}
          </Typography>
          <Typography variant="body1">
            <strong>Example:</strong> {wordDetails.Example}
          </Typography>
        </CardContent>
      </Card>
    )}
  </Box>
);
};

export default SiteStatistics;
